import osimport shutil# Set the directory pathdirectory = "."# Get all files in the directoryfiles = os.listdir(directory)# Iterate through the list of filesfor file in files:    # Get the first character of the file name    first_char = file[0]        # Create a subdirectory with the first character as its name    subdirectory = os.path.join(directory, first_char)    if not os.path.exists(subdirectory):        os.mkdir(subdirectory)        # Move the file to the subdirectory    file_path = os.path.join(directory, file)    new_file_path = os.path.join(subdirectory, file)    shutil.move(file_path, new_file_path)